/* --- App --- */
var app = {
	sizes: {},
	viewport: {},
	device: {},
	utils: {}
};

/* --- Config --- */
app.config = {
	isMobile: 1025,
	duration: 350,
	renderDelay: 1000 / 60,
	scrollReady: true
};

/* --- DOM --- */
