function submitForm() {
	$.each($('.js-ajaxForm'), function () {
		var $form = $(this),
		    action = $form.attr('data-action') || 'callback',
		    href = $form.attr('data-href') || '',
		    data = new FormData();

		$form.on('submit', function () {
			if (!$form.hasClass('loading')) {

				$.each($form.serializeArray(), function (index, value) {
					if (value.value) data.append(value.name, value.value);
				});

				data.append('action', action);

				$form.addClass('loading');
				$form.find('input, button, textarea, select').attr('disabled', 'disabled');

				$.ajax({
					url: href,
					data: data,
					dataType: 'json',
					processData: false,
					contentType: false,
					type: 'POST',
					success: function success(json) {
						if (json.status == 'ok' || json.status == "success" || json.status == 200) {

							app.utils.responseForm('#thanksPopup');
							$form[0].reset();

							$form.removeClass('loading');
							$form.find('input, button, textarea, select').removeAttr('disabled');
						} else console.log('status error');
					}
				});
			}
		});
	});
}

