function counterTime($counter, maxVal, minVal) {
	var $plus = $counter.find('.js-counter-plus'),
	    $minus = $counter.find('.js-counter-minus'),
	    $input = $counter.find('.js-counter-input'),
	    maxVal = parseInt($input.attr('max'), 10),
	    minVal = parseInt($input.attr('min'), 10);

	$input.unbind('focus, keyup, keypress, blur');

	$input.on('keypress', function (e) {
		if (e.which != 8 && e.which != 0 && e.which != 46 && (e.which < 48 || e.which > 57) || e.which == 46 && $(this).val().indexOf('.') < 0) return false;
	});

	$input.on('keyup blur', function (e) {
		if (isNaN(this.value) || this.value === undefined || this.value === '') this.value = '0' + minVal;else {
			var newVal = parseInt(this.value, 10);
			if (newVal < 10) newVal = '0' + newVal;

			this.value = newVal;
		}

		if (this.value > maxVal) this.value = maxVal;
		if (this.value < minVal) this.value = '0' + minVal;
	});

	$plus.unbind('click').on('click', function () {
		plus();
		return false;
	});

	$minus.unbind('click').on('click', function () {
		minus();
		return false;
	});

	function plus() {
		var val = parseInt($input.val(), 10),
		    newVal = val + 1;

		if (isNaN(val) || val === undefined) newVal = minVal;
		if (newVal > maxVal) newVal = minVal;
		if (newVal < minVal) newVal = maxVal;
		if (newVal < 10) newVal = '0' + newVal;

		$input.focus().val(newVal).attr('value', newVal);

		return newVal;
	}

	function minus() {
		var val = parseInt($input.val(), 10),
		    newVal = val - 1;

		if (isNaN(val) || val === undefined) newVal = minVal;
		if (newVal > maxVal) newVal = minVal;
		if (newVal < minVal) newVal = maxVal;
		if (newVal < 10) newVal = '0' + newVal;

		$input.focus().val(newVal).attr('value', newVal);

		return newVal;
	}
}

