function filterSearchInit() {
	$.each($('.js-filterSearch'), function () {
		var $form = $(this),
		    $typeahead = $form.find('.js-typeahead'),
		    checkInput = function checkInput($input) {
			var $label = $input.closest('label');

			if ($input.val() != '') {
				$label.addClass('notEmpty');
			} else {
				$label.removeClass('notEmpty');
			}
		};

		$form.on('submit', function () {
			submitForm($(this));
		});

		$.each($typeahead, function () {
			checkInput($(this));
		});

		$typeahead.on('keyup', function () {
			checkInput($(this));
		});

		getDataRoute($typeahead, 'getDataRoute');

		function submitForm($form) {
			var href = $form.attr('data-href') || '';
			var action = $form.attr('data-action') || 'filterSearch';
			var data = new FormData();

			if (!$form.hasClass('loading')) {

				$.each($form.serializeArray(), function (index, value) {
					if (value.value) data.append(value.name, value.value);
				});

				data.append('action', action);

				$form.addClass('loading');
				$form.find('input, button, textarea, select').attr('disabled', 'disabled');

				$.ajax({
					url: href,
					data: data,
					dataType: 'json',
					processData: false,
					contentType: false,
					type: 'POST',
					success: function success(json) {
						if (json.status == 'ok' || json.status == "success" || json.status == 200) {

							app.utils.responseForm('#thanksPopup');
							$form[0].reset();

							$form.removeClass('loading');
							$form.find('input, button, textarea, select').removeAttr('disabled');
						} else if (json.status == 'notFound') {

							app.utils.responseForm('#flightRequestPopup');
						} else console.log('status error');
					}
				});
			}
		};
	});
}

