// click for map
$(document).on('click', '.js-clickToMap', function () {
	var $object = $('.js-scrollToMap');

	if ($object.hasClass('openMap--half')) {
		$object.addClass('openMap--full');
	} else {
		$object.addClass('openMap--half');
	}
});
// scroll for map
function scrollToMap() {
	$.each($('.js-scrollToMap'), function () {
		disableScroll($(this));
		isMobile($(this));
	});

	function enableScroll($object) {
		$object.on('mousewheel', function (event) {
			if (event.deltaY < 0) {
				if ($object.hasClass('openMap--half')) {
					$object.addClass('openMap--full');
				} else {
					$object.addClass('openMap--half');
					disableScroll($object);
				}
			} else {
				if (!$object.hasClass('openMap--full')) {
					$object.removeClass('openMap--half');
				}
			}
		});
	};

	function disableScroll($object) {
		$object.off('mousewheel');

		setTimeout(function () {
			enableScroll($object);
		}, 1000);
	};

	function isMobile($object) {
		var object = new Hammer($object[0], { threshold: 1, velocity: 0.1 });

		if (app.device.isMobile) {
			$('.js-clickToMap').text($('.js-clickToMap').attr('data-mobile-text') || 'Click here or swipe for map');
			//- show half search for mobile
			object.on('panup swipeup swipeleft swiperight', function () {
				if ($object.hasClass('openMap--half')) {
					$object.addClass('openMap--full');
				} else {
					$object.addClass('openMap--half');
				}
			});
			//- hide half search for mobile
			object.on('pandown swipedown', function () {
				if (!$object.hasClass('openMap--full')) {
					$object.removeClass('openMap--half');
				}
			});
		}
	}
}
// init google map
function initMap() {
	if ($('#googleMap').length != 0) {
		var myLatlng = new google.maps.LatLng(40.6976637, -74.119764);
		var mapOptions = {
			center: myLatlng,
			zoom: 5,
			mapTypeControl: false,
			mapTypeId: 'hybrid',
			disableDefaultUI: true,
			labels: true,
			draggable: false
		};
		var map = new google.maps.Map(document.getElementById('googleMap'), mapOptions);
	}
}
// change one-way/round-trip
function changeRoundTrip($form) {
	var $fromDate = $form.find('.datepicker--from'),
	    $toDate = $form.find('.datepicker--to'),
	    $gmiDates = $form.find('.js-gmi-datepicker-period'),
	    $caption = $form.find('.js-date-caption'),
	    $radio = $form.find('.js-changeWay'),
	    defaultText = $caption.html(),
	    altText = $caption.attr('data-text');

	$radio.on('change', function () {
		if ($form.hasClass('round')) {
			$form.removeClass('round');
			$fromDate.datepicker('option', 'maxDate', null);
			gmiInit('date');
			$caption.html(defaultText);
		} else {
			$form.addClass('round');
			gmiInit('date-range');
			$caption.html(altText);
			$fromDate.datepicker('option', 'maxDate', getDate($toDate[0]));
		}
	});

	// --- calendar -----
	$fromDate.datepicker({
		defaultDate: "+1w"
	}).on("change", function () {
		$toDate.datepicker("option", "minDate", getDate(this));
	});

	$toDate.datepicker({
		defaultDate: "+1w"
	}).on("change", function () {
		$fromDate.datepicker("option", "maxDate", getDate(this));
	});

	function getDate(element) {
		var date;

		try {
			date = $.datepicker.parseDate("mm/dd/yy", element.value);
		} catch (error) {
			date = null;
		}

		return date;
	}

	//--- gmi-calendar -----
	if ($form.hasClass('round')) gmiInit('date-range');else gmiInit('date');

	function gmiInit(typeCalendar) {
		$gmiDates.gmiDatepicker('clear').gmiDatepicker('destroy').gmiDatepicker({
			type: typeCalendar,
			readonly: true,
			format: 'MM/dd/yyyy',
			placeholder: 'select date',
			onShow: function onShow() {}
		});

		if ($gmiDates.hasClass('js-gmi-datepicker--black')) {
			$('.gmi-picker-panel').addClass('gmi-picker-panel--black');
		}

		if (app.device.isMobile) {
			if ($('.gmi-picker-panel').find('.js-gmi-datepicker-close').length === 0) {
				$('.gmi-picker-panel').prepend('<div class="gmi-picker-panel__close js-gmi-datepicker-close">close</div>');

				$('.js-gmi-datepicker-close').on('click', function () {
					$gmiDates.gmiDatepicker('hide');
				});
			}
		}
	}
}
// sidebar result list item hover
function sidebarResultsHover() {
	var $items = $('.js-sidebarListItem'),
	    $detailBlocks = $items.find('.js-sidebarListItemDetail'),
	    open = function open($item) {
		$detailBlocks.stop().slideUp(400);
		$item.addClass('open');
		$item.find('.js-sidebarListItemDetail').stop().slideDown(400);
	},
	    close = function close($item) {
		$items.removeClass('open');
		$item.find('.js-sidebarListItemDetail').stop().slideUp(400);
	};

	if (app.device.isMobile) {
		$items.on('click', function () {
			var $item = $(this);

			if ($item.hasClass('open')) {
				close($item);
			} else {
				open($item);
			}
		});
	} else {
		$items.on('mouseenter', function () {
			open($(this));
		});

		$items.on('mouseleave', function () {
			close($(this));
		});
	}
}
// switch price range
function switchPriceRange($form) {
	var $rangeFromPriceInput = $form.find('.js-price-range-from'),
	    $rangeToPriceInput = $form.find('.js-price-range-to'),
	    $rangePriceBox = $rangeFromPriceInput.closest('.js-price-box'),
	    $captionPriceRange = $form.find('.js-price-range-caption'),
	    $captionMaxPrice = $form.find('.js-price-max-caption'),
	    $maxPriceInput = $form.find('.js-price-max'),
	    $maxPriceBox = $maxPriceInput.closest('.js-price-box'),
	    checkRange = function checkRange() {
		var valFrom = $rangeFromPriceInput.val().replace(/[\$\,]/g, '') || 0,
		    valTo = $rangeToPriceInput.val().replace(/[\$\,]/g, '') || 0;

		if (+valFrom > +valTo) {
			$form.addClass('error');
		} else {
			$form.removeClass('error');
		}
	};

	$rangeFromPriceInput.on('keyup change', function () {
		$maxPriceInput.val('');
		checkRange();
	});

	$rangeToPriceInput.on('keyup change', function () {
		$maxPriceInput.val('');
		checkRange();
	});

	$maxPriceInput.on('keyup change', function () {
		$rangeFromPriceInput.val('');
		$rangeToPriceInput.val('');

		$form.removeClass('error');
	});

	$rangePriceBox.on('click', function () {
		$rangeFromPriceInput.removeAttr('disabled');
		$rangeToPriceInput.removeAttr('disabled');
		$maxPriceInput.attr('disabled', 'disabled');

		$captionMaxPrice.removeClass('active');
		$captionPriceRange.addClass('active');
	});

	$maxPriceBox.on('click', function () {
		$rangeFromPriceInput.attr('disabled', 'disabled');
		$rangeToPriceInput.attr('disabled', 'disabled');
		$maxPriceInput.removeAttr('disabled');

		$captionPriceRange.removeClass('active');
		$captionMaxPrice.addClass('active');
	});
}

