//- action on scroll
function checkAction() {
	$('.actionBox').not('.noAction').filter(':in-viewport').each(function () {
		var $box = $(this);

		$box.addClass('showed');

		if ($box.data('action')) {
			if (!$box.data('action').flag_box) {
				$box.data('action').flag_box = true;
				$box.data('action').actionBox();
			}
		}
	});
}
//- tabs
function tabsInit() {
	$.each($('.js-tabBox'), function () {
		var $box = $(this),
		    $navItems = $box.find('.js-tabNavItem'),
		    $activeNavItem = $navItems.filter('.active'),
		    $contentItems = $box.find('.js-tabContentItem'),
		    activeId = $activeNavItem.attr('data-id') || 0,
		    $activeContentItem = $contentItems.filter('[data-id="' + activeId + '"]');

		if ($activeNavItem.length === 0) {
			//- if there are no active class in the tabs, we do the first in order
			$activeNavItem = $navItems.eq(0);
			activeId = $activeNavItem.attr('data-id');
			$activeContentItem = $contentItems.filter('[data-id="' + activeId + '"]');

			$activeNavItem.addClass('active');
			$activeContentItem.fadeIn(200, function () {
				$contentItems.removeClass('active');
				$(this).addClass('active');
			});
		} else {
			$contentItems.removeClass('active');
			$activeContentItem.fadeIn(200, function () {
				$(this).addClass('active');
			});
		}

		$navItems.on('click', function () {
			var $navItem = $(this),
			    id = $navItem.attr('data-id'),
			    $prevActiveContentItem = $contentItems.filter('.active'),
			    $activeContentItem = $contentItems.filter('[data-id="' + id + '"]'),
			    $form = $prevActiveContentItem.find('form'),
			    clearForm = function clearForm($form) {
				var $dateFrom = $form.find('.datepicker--from');
				var $dateTo = $form.find('.datepicker--to');

				$form.find('input[type="text"], input[type="email"], input[type="number"], input[type="password"]').val('');

				$.each($form.find('input[type="checkbox"]'), function () {
					$(this)[0].checked = false;
					$(this).closest('label').removeClass('checked');
				});

				if ($dateFrom.length > 0) $dateFrom.datepicker('option', 'maxDate', null);
				if ($dateTo.length > 0) $dateTo.datepicker('option', 'minDate', null);

				$form.find('label').removeClass('error');
			};

			if ($form.length != 0 && $form.attr('data-clear') != 'off') {
				clearForm($form);
			}

			$navItems.removeClass('active');
			$navItem.addClass('active');

			$contentItems.removeClass('active');

			$prevActiveContentItem.fadeOut(200, function () {
				$activeContentItem.fadeIn(200, function () {
					$activeContentItem.addClass('active');
				});
			});
		});
	});
}
//- accordion on hover
function accordionHover() {
	var $items = $('.js-acc-hover-item'),
	    $contents = $items.find('.js-acc-hover-content');

	$items.on('mouseenter', function () {
		var $item = $(this),
		    $content = $item.find('.js-acc-hover-content');

		if (!$content.hasClass('show') && !$item.hasClass('open')) {
			$contents.stop().slideUp(400).removeClass('show');
			$content.stop().slideDown(400).addClass('show');
		}
	});

	$items.on('mouseleave', function () {
		var $item = $(this),
		    $content = $item.find('.js-acc-hover-content');

		$content.stop().slideUp(400).removeClass('show');
	});
}
//- custom scroll
function customScrollInit() {
	$.each($('.js-customScroll'), function () {
		var positon = 'inside',
		    direction = 'y',
		    $scrollBox = $(this);

		if ($scrollBox.attr('data-outside') != undefined) positon = 'outside';
		if ($scrollBox.attr('data-horizontal') != undefined) direction = 'x';

		if (app.device.isMobile) {
			$scrollBox.css({
				overflow: 'auto'
			});
		} else {
			$scrollBox.mCustomScrollbar({
				axis: direction,
				scrollInertia: 500,
				autoHideScrollbar: true,
				scrollbarPosition: positon,
				advanced: {
					updateOnContentResize: true
				}
			});
		}
	});
}
//- fancybox
function fancyboxInit() {
	$('[data-fancybox]').fancybox({
		animationEffect: 'zoom',
		baseClass: 'simple-popup',
		buttons: ['close'],
		arrows: true,
		keyboard: true,
		touch: false,
		autoFocus: false,
		onInit: function onInit() {
			$('.page').addClass('blur');
		},
		beforeClose: function beforeClose(instance, current) {
			$('.page').removeClass('blur');
		}
	});
}
//- tooltip
function tooltipInit() {
	$('[data-tooltip]').on('mouseenter mousemove', function (e) {
		var $tooltip = $($(this).attr('data-tooltip')),
		    height = $tooltip.outerHeight(),
		    width = $tooltip.outerWidth();

		$tooltip.css({
			left: e.pageX - width - 15,
			top: e.pageY - height - 15
		}).addClass('show');
	}).on('mouseleave', function () {
		var $tooltip = $($(this).attr('data-tooltip'));

		$tooltip.removeClass('show');
	});
}
